INSERT INTO services (freelancer_id, category_id, title, description, price_from, price_to, price_type, region, city, views_count) VALUES
(NULL, 1, 'Создание сайта-визитки', 'Разработаю современный сайт-визитку для вашего бизнеса. Адаптивный дизайн, быстрая загрузка, SEO-оптимизация. Включает: дизайн, верстку, настройку хостинга.', 15000, 25000, 'project', 'Москва', 'Москва', 156),
(NULL, 2, 'Логотип и фирменный стиль', 'Создам уникальный логотип и фирменный стиль для вашей компании. Несколько вариантов на выбор, все исходники, руководство по использованию.', 8000, 15000, 'project', 'Санкт-Петербург', 'Санкт-Петербург', 89),
(NULL, 3, 'Копирайтинг для сайта', 'Напишу продающие тексты для вашего сайта. Опыт работы более 5 лет, портфолио по запросу. Работаю с любыми тематиками.', 500, 1500, 'hourly', 'Свердловская область', 'Екатеринбург', 234),
(NULL, 4, 'SMM продвижение', 'Ведение социальных сетей, создание контента, настройка рекламы в Instagram и ВКонтакте. Увеличение охватов и продаж.', 20000, 40000, 'project', 'Новосибирская область', 'Новосибирск', 67),
(NULL, 1, 'Интернет-магазин на WordPress', 'Создание полнофункционального интернет-магазина на WordPress + WooCommerce. Настройка платежей, доставки, админ-панели.', 35000, 60000, 'project', 'Москва', 'Москва', 203),
(NULL, 2, 'Дизайн мобильного приложения', 'UI/UX дизайн мобильного приложения. Создание пользовательских сценариев, wireframes, финальный дизайн всех экранов.', 25000, 45000, 'project', 'Санкт-Петербург', 'Санкт-Петербург', 145),
(NULL, 3, 'Рерайтинг статей', 'Качественный рерайтинг статей для сайтов и блогов. Уникальность 95%+, сохранение смысла, SEO-оптимизация.', 300, 800, 'hourly', 'Свердловская область', 'Екатеринбург', 78),
